class PersistenceManagerUseCase implements IPersistenceManager {
    private static HashMap<Class, EntityManager> entityManagers = new HashMap<Class, EntityManager>();
    private static EntityManagerFactory entityManagerFactory;
    private static ReentrantLock lock = new ReentrantLock();
    //private static EntityManagerFactory entityManagerFactory;
    //private static EntityManager entityManager;

    public static EntityManager getEntityManager(Class entityClass) throws TechnicalException {
        synchronized (lock) {
            return getEntityManager(entityClass, false);
        }
    }

    public static EntityManager getEntityManager(Class entityClass, boolean isUnitTest) throws TechnicalException {
        /*if (entityManagerFactory == null) {
            Map<String, String> properties = new HashMap<String, String>();
            entityManagerFactory = Persistence.createEntityManagerFactory("UnitTestDerby", properties);
        }

        if (entityManager == null) {
            entityManager = entityManagerFactory.createEntityManager();
        }

        return entityManager;*/

        synchronized (lock) {
            if (entityManagerFactory == null) {
                Map<String, String> properties = new HashMap<String, String>();
                try {
                    if (isUnitTest) {
                        entityManagerFactory = Persistence.createEntityManagerFactory(AppSettings.getString("UnitTestDatabaseName"), properties);
                    } else {
                        entityManagerFactory = Persistence.createEntityManagerFactory(AppSettings.getString("ProductiveDatabaseName"), properties);
                    }
                } catch (SettingException e) {
                    throw new TechnicalException(ErrorMessages.get("unreadableSettingsFile"));
                }
            }

            //entityManagerFactory.getCache().evictAll();

            if (!entityManagers.containsKey(entityClass)) {
                entityManagers.put(entityClass, entityManagerFactory.createEntityManager());
            }

            return entityManagers.get(entityClass);
        }
    }
}






































































































































































































































